--Basic Actions	--CardIndex 2012 --UtilityIndex 29
insert into Cards (Name, PictureFront, PictureBack, Link, CardType, ProductSet, ProductModule, PlayMode, Flavor)
VALUES ('Basic Actions',
'http://htkb.info/SDE/2.0/Cards/Utility/Card_Utility_Basic_Actions_2.0.png', 
'http://htkb.info/SDE/2.0/Cards/Utility/Card_Utility_2.0_DI_back.png', 
'http://htkb.online/SDWiki/SDE/2.0/Explore/Cards/Utility_Cards/Basic_Actions.xhtml', 
'Utility', '2.0', '2.0 Core Set', 'Arcade', '');
insert into Utilities (CardIndex, UtilityDescription) VALUES (2012, 'BASIC ACTIONS pg. 19 <br>Magic Attack: Magic, Make an offensive action using WILL.</br><br><br>Melee Attack: Melee, Make an offensive action using STR.</br><br><br>Missile Attack: Missile, Make an offensive action using DEX.</br><br><br>Dash: Move to squares.</br><br><br>Open Chest: Open an adjacent Treasure Chest.</br><br><br>Interact: Augment, The model interacts with an element of the scenario as described in the scenario.</br><br><br>Loud Slurping: Augment, the Hero gains 2 wrath tokens.</br><br><br>POTIONS pg. 18</br><br>Once per turn, a Hero may discard a potion token to take a potion action without spending action points.</br><br><br>Blue Potion: Make the listed support action.</br><br><br>Red Potion: Make the listed offensive action.</br><br><br>Green Potion: Make the listed support action. Green potions may only be used during the Consul''s turn.</br>');

