insert into Utilities (CardIndex, UtilityDescription) VALUES (2013, 'BASIC ACTIONS pg. 19 <br>Magic Attack: Magic, Make an offensive action using WILL.</br><br><br>Melee Attack: Melee, Make an offensive action using STR.</br><br><br>Missile Attack: Missile, Make an offensive action using DEX.</br><br><br>Dash: Move to squares.</br><br><br>Open Chest: Open an adjacent Treasure Chest.</br><br><br>POTIONS pg. 18</br><br>Once per turn, a Hero may discard a potion token to take a potion action without spending action points.</br><br><br>Blue Potion: Make the listed support action.</br><br><br>Red Potion: Make the listed offensive action.</br><br><br>Green Potion: Make the listed support action. Green potions may only be used during the Consul''s turn.</br>');

--Hero Turn Summary	--CardIndex 2014 --UtilityIndex 31
insert into Cards (Name, PictureFront, PictureBack, Link, CardType, ProductSet, ProductModule, PlayMode, Flavor)
VALUES ('Hero Turn Summary',
'http://htkb.info/SDE/2.0/Cards/Utility/Card_Utility_Basic_Actions_2.0_DI_back.png', 
'http://htkb.info/SDE/2.0/Cards/Utility/Card_Utility_2.0_DI_back.png', 
'http://htkb.online/SDWiki/SDE/2.0/DI/Explore/Cards/Utility_Cards/Hero_Turn_Summary.xhtml', 
'Utility', '2.0 DI', '2.0 Devil Island Set', 'Explore', '');
insert into Utilities (CardIndex, UtilityDescription) VALUES (2014, 'HERO TURN SUMMARY pg. 13</br><br>Perform the following:</br><br><br>Complete one Hero activation</br><br>Complete a second Hero activation</br><br>Power-Up Phase</br><br><br>HERO ACTIVATION pg. 14</br><br>When activating a Hero perform the following:</br><br>Add an activation token to the Hero''s card</br><br>Upkeep</br><br>Movement and Actions - in any order and combination</br><br><br> HERO POWER-UP pg. 20</br><br>Perform the following during Power-Up:</br><br>Rewards, pg. 20</br><br>Princess Coins, pg. 21</br><br>Equipment</br><br><br>TYPES OF ACTIONS pg 15</br><br>Red offensive actions inflict a single wound if successful.</br><br><br>Blue support actions do not inflict wounds, instead they will list other effects.</br><br><br>Augment actions only affect friendly models. Augment actions that do not list a range affect the model using the action.</br><br><br>Dangerous actions will affect both friendly and enemy models.</br>');

