/*My Brother!							--Index 765*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'My Brother!', 'When Ugg is spawned, also spawn Mugg adjacent to the SPAWNPOINT on the first tile (moving any models necessary to place him).  Mugg also counts as a Dungeon Boss and the Heroes do not win until both Dungeon Bosses are destroyed.  Time Out is reached when one of the Dungeon Bosses is destroyed.');
