insert into Utilities (CardIndex, UtilityDescription) VALUES (3026, 'BASIC ACTIONS pg. 19 <br>Magic Attack: Magic, Make an offensive action using WILL.</br><br><br>Melee Attack: Melee, Make an offensive action using STR.</br><br><br>Missile Attack: Missile, Make an offensive action using DEX.</br><br><br>Dash: Move to squares.</br><br><br>Open Chest: Open an adjacent Treasure Chest.</br><br><br>Interact: Augment, The model interacts with an element of the scenario as described in the scenario.</br><br><br>Loud Slurping: Augment, the Hero gains 2 wrath tokens.</br><br><br>POTIONS pg. 18</br><br>Once per turn, a Hero may discard a potion token to take a potion action without spending action points.</br><br><br>Blue Potion: Make the listed support action.</br><br><br>Red Potion: Make the listed offensive action.</br><br><br>Green Potion: Make the listed support action. Green potions may only be used during the Consul''s turn.</br>');

