--2.0 Keywords

/*Get 'em Boys							--Index 416*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Get ''em Boys', 'Spawn four Spikers.  Starting with the Player(s), the Consul and the Party alternate placing Spikers in the dungeon until all four are placed.  Spikers may not be placed within three squares of another Spiker or in the first tile.');

/*9 Lives								--Index 417*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', '9 Lives', 'One Use Only.  When a model with 9 Lives is destroyed, immediately roll 1R.  Remove one wound token for each star rolled.  If no stars are rolled, the model is destroyed as normal.');
/*Active Spawn							--Index 418*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Active Spawn', 'Spawning points with Active Spawn may activate during the Consul Turn in the same manner as 16-Bit Monsters.  When destroyed they grant loot as normal for spawning points.');
/*Active Spawn							--Index 419*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Active Spawn', 'Spawning points with Active Spawn may activate during the Consul Turn in the same manner as 16-Bit Monsters.');
/*Afflict								--Index 420*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Afflict', 'A model with Afflict X causes its target to suffer the listed status effect(s) when it achieves a critical success.');
/*Afflict								--Index 421*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Afflict', 'POISON, IMMOBILE');
/*Afflict								--Index 422*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Afflict', 'FIRE, KNOCKDOWN');
/*Afflict X								--Index 423*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Afflict X', 'A model with Afflict X causes its target to suffer the listed status effect(s) when it achieves a critical.');
/*Afflict X								--Index 424*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Afflict X', 'A model with Afflict X causes its target to suffer the listed status effect(s) when it achieves a critical success.');
/*Alchemy								--Index 425*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Alchemy', 'When a model with Alchemy rolls a heart, it may choose to gain a potion instead.');
/*All-Seeing							--Index 426*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'All-Seeing', 'This Pet''s Master gains Hookshot on its basic Magic attack if it has one and it began its activation within two squares of this Pet.');
/*Alone Forever							--Index 427*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Alone Forever', 'Other friendly models within three squares of a model with Alone Forever must discard the highest result rolled anytime they make a defense roll.');
/*Ambush 								--Index 428*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Ambush', 'When this model spawns or enters play using a Princess Coin, it may be placed on any open square in the dungeon.');
/*Ambush Predator						--Index 429*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Ambush Predator', 'After an enemy model enters a square adjacent to a model with Ambush Predator, the model with Ambush Predator may make a basic attack action against it.');
/*Ammo Dump								--Index 430*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Ammo Dump', 'The Consul places six crystal shell tokens anywhere in the dungeon.  These tokens may not be placed within three squares of another crystal shell token.<br><br>All Heroes gain the following action for the duration of this Boss Fight:  Firing Pin');
/*Angry Walrus							--Index 431*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Angry Walrus', 'At the end of the Consul''s power-up phase, the Consul rolls 1B. If no stars are rolled, nothing happens. If any stars are rolled, the Consul may move the model with Angry Walrus up to one square for each star rolled and then make a basic melee offensive action against any other member of the party in range (Arcade: the Hero''s controller rolls and must move towards and attack the closest other friendly model if stars are rolled).');
/*Animate Soul							--Index 432*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Animate Soul', 'Place a crystal token on target Hero.  When the Hero is destroyed, if the model that placed the crystal is not in play, remove the crystal adjacent to the Hero.  The model spawned in this way suffers three wounds after being placed.');
/*Animate Soul							--Index 434*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Animate Soul', 'Place a crystal token on target Hero.  When the Hero is destroyed, if the model that placed the crystal is not in play, remove the crystal token to spawn the model that placed the crystsal adjacent to the Hero. The model spawned in this way suffers three wounds after being placed.');
/*Another Round							--Index 435*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Another Round', 'During upkeep roll 1B.  For every start rolled Deodric gains one potion.');
/*Apprenticeships						--Index 436*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Apprenticeships', 'Spawn six Forge Gnomes; one within two squares of each dungeon exit in the first tile and one within two squares of each dungeon exit in the last tile.');
/*Arc									--Index 437*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Arc', 'After resolving an action with Arc, choose another model within two squares of the last model targeted and which has not already been targeted.  Resolve the action again, rolling one less dice of the player''s choice against the new target.  Continue choosing new targets in this manner until Arc fails to inflict a wound, no dice remain, or there is no new target in range.');
/*Area Effects							--Index 438*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Area Effects', 'Some actions can hit multiple models all at the same time. These actions are called area effects. To use an area effect, first determine which models are in squares that will be affected. Squares that the user cannot draw line of sight to are not affected. All models that are within the area are considered to be affected simultaneously.\r\n\r\nWith the exception of Burst X, area effects replace the range of an action. If the area effect action requires an offense roll the model using the action makes a single roll. Every model within the area is allowed to make a defense roll. Friendly models, including the user, are not affected by area effects unless they are Augment or Dangerous actions. If a model is affected by two area effects simultaneously the effects are cumulative as long as the actions’ names are different, even if their effects are the same.');
/*Armory								--Index 439*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Armory', 'Equip three Relic Items (in addition to any equipped due to remaining spawning points).');
/*Artillery								--Index 440*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Artillery', 'An action with Artillery may target any square within range, even if no model occupies the square.');
/*As You Wish							--Index 441*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'As You Wish', 'After a treasure with As You Wish is equipped the party must immediately draw two treasure cards and equip them both. While an item with As You Wish is in play, all Heroes with at least one treasure suffer a wound when they achieve a critical success.');
/*Augment 								--Index 442*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Augment', 'May only target or affect friendly models.');
/*Autosave								--Index 443*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Autosave', 'One Use Only. When this model or a friendly model within 2 squares would be destroyed, immediately roll 1G. Remove one wound token for each star rolled. If no stars are rolled, the model is destroyed as normal.');

/*Back Strike							--Index 444*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 'Back Strike', '+1ST STR If the gang''s target did not activate during the previous Hero Turn.');
/*Backlash								--Index 445*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Backlash', 'When a model with Backlash is targeted by an offensive action and wins the defense roll, the model making the offense roll suffers one wound.  A model may not draw a loot card for destroying a model due to Backlash.');
/*Backlash								--Index 446*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 'Backlash', 'If a model targeted by an offensive action and wins the defense roll, the model making the offense roll suffers one wound.  A model may not draw a loot card for destroying a model due to Backlash.');
/*Backlash								--Index 447*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 'Backlash', 'If a model targeted by an offensive action possesses Backlash and wins the defense roll, the model making the offense roll suffers one wound.  A model may not draw a loot card for destroying a model due to Backlash.  (Arcade: A Hero may not inflict more than one wound to monsters in this way per Consul Turn.)');
/*Backlash								--Index 448*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Backlash', 'If a model targeted by an offensive action and wins the defense roll, the model making the offense roll suffers one wound.  A model may not draw a loot card for destroying a model due to Backlash.');
/*Backlash								--Index 449*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Backlash', 'If a model targeted by an offensive action possesses Backlash and wins the defense roll, the model making the offense roll suffers one wound.  A model may not draw a loot card for destroying a model due to Backlash.');
/*Backstabber							--Index 450*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Backstabber', 'When a model with Backstabber suffers a wound, place the wound token on a friendly minion model wihtin three squares instead.');
/*Backstabber							--Index 451*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Backstabber', 'When a model with Backstabber suffers a wound, it may place the wound token on an adjacent friendly model instead.');
/*Bag of Crows							--Index 452*/	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bag of Crows', 'When a model with Bag of Crows succeeds on a defense roll, spawn one Underdead Crow adjacent to it for each heart rolled (up to a maximum of 4 Underdead Crows in play).');
/*Bane									--Index 453*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bane', 'A model suffering Bane discards the highest result rolled anytime it makes a defense roll.');
/*Bane									--Index 454*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 'Bane', 'A monster suffering Bane reduces its ARM by 1ST.');
/*Bane									--Index 455*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bane', 'A model suffering Bane discards the highest result rolled anytime it makes a defense roll. (Arcade: A monster suffering Bane reduces its ARM by 1ST.)');
/*Battle Without End					--Index 456*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Battle Without End', 'If no mini-boss is in play, spawn a mini-boss selected for this game adjacent to target Hero.  The mini-boss immediately suffers three wounds.  Heroes do not draw treasure for destroying this mini-boss.');
/*Berserk								--Index 457*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Berserk', 'During its activation, a model with Berserk may make a single Melee Attack costing no action points.');
/*Bewitching Song						--Index 458*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bewitching Song', 'The Consul may move all Heroes 3 squares, using the rules for Compel.');
/*Birds of a Feather					--Index 459*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Birds of a Feather', 'When a model with Birds of a Feather suffers a wound, spawn 2 Underdead Crows adjacent to it.');
/*Biting Wind							--Index 460*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Biting Wind', '');
/*Black Ice								--Index 461*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Black Ice', 'Enemy models which end an activation within two squares of this model are moved two squares directly away from the model with Black Ice.');
/*Black Parade							--Index 462*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Black Parade', 'When a model with Black Parade enters play, the Consul spawns two Curse Ravens adjacent to the spawning point icon in the third tile and two adjacent to the spawning point icon in the second tile (four Curse Ravens total).');
/*Bleed									--Index 463*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bleed', 'When making an offensive roll, a model with Bleed counts any Hearts rolled as both one star and one heart.');
/*Blessed Strike						--Index 464*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Blessed Strike', 'A model with Blessed Strike gains +1ST to all offense rolls it makes for each spent Princess Coin on its card.');
/*Blessing								--Index 465*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Blessing', 'A tile effect with Blessing bestows a positive effect, specified on the tile effect''s card, to models within the tile. Blessings only affect Hero, elite and minion models.');
/*Blood Drinker							--Index 466*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 	'Blood Drinker', 'If a monster with Blood Drinker inflicts at least one wound during a Fight command, remove one wound token from its card.');
/*Blood Drinker							--Index 467*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Blood Drinker', 'When a model with Blood Drinker inflicts a wound, it may remove one wound or status effect token from its card for every heart rolled.');
/*Bluff									--Index 468*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bluff', 'Models with Bluff gain +1ST to offensive actions taken during another model''s activation.');
/*Bob-omb								--Index 469*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bob-omb', 'If another Hero activates after this Hero, during the same turn, that Hero gains +1R to the first offense roll it makes that activation.');
/*Bodyguard								--Index 470*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bodyguard', 'Mini-Bosses and Dungeon Bosses that are adjacent to a monster with Bodyguard may not be targeted with offensive actions which can legally target the monster with Bodyguard.');
/*Bonded Creep							--Index 471*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bonded Creep', 'This Creep may not be used unless the listed Dungeon Boss is selected for this game.');
/*Bone Collector						--Index 472*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bone Collector', 'A model with Bone Collector may use hearts it rolls during successful offensive actions on Pets as well as Heroes.');
/*Boo Booty								--Index 473*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Boo Booty', 'Boo Booty cannot be spawned, instead they enter play when their treasure card is drawn.');
/*Born Lucky							--Index 474*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Born Lucky', 'A model with Born Lucky may reroll one heart when it rolls dice.');
/*Bouncing Betty						--Index 475*/	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bouncing Betty', 'A model with Bouncing Betty may enter squares occupied by other models (and other models may enter squares containing a model with Bouncing Betty).  As soon as a model with Bouncing Betty shares a square with an enemy model, destroy it and trigger Volatile.  When a model with Bouncing Betty activates, roll 1R and move it a number of squares equal to the number of stars rolled towards the closest Hero.  If there are ever 12 models with Bouncing Betty in play, immediately destroy all of them (this triggers Volatile).');
/*Bound									--Index 476*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bound', 'When a monster with Bound is destroyed, a SUPER on the same tile may instead choose to suffer one wound and prevent the monster from being destroyed.');
/*Bound									--Index 477*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Bound', 'When a monster with Bound is destroyed, a SUPER on the same tile may instead choose to suffer one damage and prevent the monster from being destroyed; remove all wound tokens from the monster with Bound.');
/*Broken Heart							--Index 478*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Broken Heart', 'When a model with Broken Heart rolls hearts during a successful attack, it may not use them to heal friendly models. Instead, it must choose one friendly model to suffer a wound for each heart rolled.');
/*Bruiser								--Index 479*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 'Bruiser', '+1ST STR if a model with Bruiser is in the gang.');
/*Brutal Strike							--Index 480*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Brutal Strike', 'When this model wins an offense roll, add three stars to its total.');
/*Brutal Strike							--Index 481*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Brutal Strike', 'When this model wins an offense roll, add +3ST to its total.');
/*Brutal Strike							--Index 482*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Brutal Strike', 'When this model wins an offense roll, it gains +3ST to its total.');
/*Brutal Strike							--Index 483*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Brutal Strike', 'When this model wins an offense roll, add +3ST to its total.');
/*Brutality Aura						--Index 484*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Brutality Aura', 'All models, friendly or enemy, within 2 squares gain +1st STR.');
/*Brutality Aura						--Index 485*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Brutality Aura', 'All models, friendly or enemy, within two squares of a model with Brutality Aura gain +1ST STR.');
/*Brutality Aura						--Index 486*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Brutality Aura', 'All models, friendly or enemy, that are within two squares of a model with Brutality Aura gain +1ST STR.');
/*Bulldozer								--Index 487*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bulldozer', 'A model with Bulldozer may move through enemy models, and treats difficult terrain and structure squares as open squares. All non-blessing tile effects in those squares are ignored.');
/*Bully									--Index 488*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bully', 'A monster with Bully always targets the Hero with the least wrath with its commands.');
/*Bumper Shell							--Index 489*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Bumper Shell', 'Make a Melee Attack against every Hero in the dungeon, regardless of its location.  Any model which is wounded must discard one equipped equipment card.');
/*Burn, Insects!						--Index 490*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Burn, Insects!', 'Enemy models within two squares that resolve Fire during their upkeep must discard two equipment cards from the backpack or suffer an additional wound.');
/*Burning Blades						--Index 491*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Burning Blades', 'All elite and minion monsters gain the ability FIRE.');
/*Burrow								--Index 492*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Burrow', 'A monster with Burrow may move through walls, and treats difficult terrain and structure squares as open squares.  All non-blessing tile effects in those squares are ignored.');
/*Burrow								--Index 493*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Burrow', 'A model with Burrow may move through walls, and treats difficult terrain and structure squares as open squares.  All non-blessing tile effects in those squares are ignored.');

/*Call Of The Deep						--Index 494*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Call Of The Deep', 'When a model with Call Of The Deep succeeds with an attack, it may spawn one Skull Point worth of 8-Bit monsters per heart rolled within 3 squares of it.');
/*Caught Red Handed						--Index 495*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Caught Red Handed', 'The Consul selects one Hero that is not on Kringle''s Nice List.  The selected Hero shapeshifts into a Naughty of the Consul''s choice.');
/*Cave-In								--Index 496*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Cave-In', 'Each Hero must choose to suffer either two wounds or Immobile.');
/*Chained to Me							--Index 497*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Default', 'Chained to Me', 'Enemy models that make an attack controlled by this model gain +1G to the roll.');
/*Challenge								--Index 498*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 	'Challenge', 'A model with Challenge may not suffer wounds from actions, unless the action was taken by the Hero with the most wrath (it still suffers effects such as Push and status effects normally).');
/*Challenge								--Index 499*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Challenge', 'When a boss with Challenge spawns, the Party must choose one Hero as their Champion. No model except the Champion may deal damage to the boss, though the boss may suffer other effects (such as Push or status effects) caused by other models. The boss gains +1R to all offensive actions it takes that target the Champion. If the Champion is destroyed, the Party must select another Hero to become the Champion.');
/*Challenge								--Index 500*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Explore', 'Challenge', 'When a boss with Challenge spawns, the party must choose one Hero as their Champion. No model except the Champion may deal wounds to the boss with actions, though the boss may suffer other effects (such as Push or status effects) caused by other models. If the Champion is destroyed, the Party must select another Hero to become the Champion.');
/*Charge								--Index 501*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('2.0', 'Arcade', 	'Charge', 'When a model with Charge uses a Lance area effect, after resolving all other effects of the Lance, place the model in an unoccupied square that was affected by the Lance, and which is as close as possible to the Hero with the most wrath.');
