/*Mob						--Index 230*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Mob', 'When making a Melee offensive action, a model with Mob adds +1b STR to their offense roll for every additional model with Mob that is in range of their target, up to +3b STR.');
