/*Reach						--Index 250*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Reach', 'This ability allows the model to target an enemy with a melee attack up to X squares away. A model may use any of its abilities or special actions using Reach X, unless specified otherwise.');
/*Regenerate				--Index 251*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Regenerate', 'Roll 1b for every wound token on the model using Regenerate. Remove one token for every star rolled.');
/*Rock Aura					--Index 252*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Rock Aura', 'Monsters with Sapphire affinity that are within 2 squares of Rock Pile are Immune: Status Effects.');
/*Savage					--Index 253*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Savage', 'All minions within 1 square of a model with Savage gain +1st STR.');
