/*Small						--Index 260*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Small', 'An enemy model may only target a small model if it is within 3 squares of the model.');
/*Smooches					--Index 261*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Smooches', 'This model immediately shapeshifts back to its original form if a friendly model moves into an adjacent square and spends 1 Action to give it a kiss.');
/*Soporific					--Index 262*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Soporific', 'When this model is destroyed inflicts the attacking model with the status effect Slow.');
/*Soporific					--Index 263*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Soporific', 'When a model with Soporific is destroyed, the model that destroyed it suffers Slow.');
/*Soporific Cloud			--Index 264*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Soporific Cloud', 'When a Hero that does not have Emerald affinity moves within 2 squares of Fungal Growth they suffer Slow.');
