/*Spongy					--Index 267*/ 	insert into Keywords (KeywordVersion, PlayMode, Name, KeywordDescription) VALUES ('FK', 'Default', 'Spongy', 'Anytime a model makes an offensive action against the Kinoshroom. The model is pushed one square away.');
